INSERT INTO `xi_company` VALUES ('116', null, '159', '上海戴盛投资管理有限公司', '上海市长宁区宣化路300号华宁大厦北塔2603', '民营公司', '101-500人', '王红燕', '人事', '13296078842', 'http://www.xiangshixi.com/upload/OTHER/2018-01-02_17-05-51_70daf.png', 'wangluck@dcmfx.com', '上海市长宁区宣化路300号华宁大厦北塔2603', '投行', '913101186810244028', 'DCM Group Global戴盛集团是一家注册在英国伦敦，提供全球外汇、贵金属、国际原油以及全球主要股票市场的股指期货等金融和金融衍生产品的专业公司，在奥克兰、伦敦、纽瓦克分别设有分支 机构。总部设立在英国伦敦，亚太总部设立于新西兰金融中心奥克兰，大中华区代表处设在上海。作为一家有着9年全球性的金融产品投资服务商，DCM戴盛集团以金融和科技的双重优势，将互联网思维、技术与外汇交易进行融合，不断推出创新型产品和项目。', null, 'www.dcmfx.com', 'http://www.xiangshixi.com/upload/COMPANY_LOGO/2018-01-02_17-03-44_44472.png', 'http://www.xiangshixi.com/upload/COMPANY_CERT/2018-01-02_17-07-01_e1304.jpg', '1', '2018-01-02 16:20:44', '0', null, null, null, null, null, '0');
