INSERT INTO `xi_company` VALUES ('118', null, '164', '上海忠信徐企业管理有限公司', '上海普陀区云岭东路235号跨国采购中心3号楼1202室', '民营公司', '101-500人', '孙砺博', '人事', '15221561124', 'http://www.xiangshixi.com/upload/OTHER/2018-01-04_14-27-58_c0abe.png', 'zxhr@zxzbgroup.com', '上海普陀区云岭东路235号跨国采购中心3号楼1202室', '券商资管', '91310107MA1G0FWQ0W', '忠信资本创立于2015年，是一家快速发展的专业资产管理公司。目前资产管理规模累计接近200亿元，持有证监会备案的私募基金管理人牌照。 忠信资本具有专业的投资能力、优秀的项目资源、完备的资产管理体系、一流的管理团队和顾问团队。 忠信资本全面涉及房地产股权、债权，和VC、PE投资。关注改革热点、国企改革、民企成长、并购领域业务，并以国际化视野发展海外市场，致力于打造中国最受信赖的金融投资管理机构。', '未填写', 'http://www.zxzbgroup.com/', 'http://www.xiangshixi.com/upload/COMPANY_LOGO/2018-01-04_14-26-12_13f21.png', 'http://www.xiangshixi.com/upload/COMPANY_CERT/2018-01-04_14-36-01_7eea5.png', '1', '2018-01-04 14:22:40', '0', null, null, null, null, null, '0');
