INSERT INTO `xi_position` VALUES ('45', '14', '交易操作岗', null, null, '上海浦东新区花木路', '2017-12-11', '2018-08-31', '09:00:00', '17:00:00', '100', '90', '元/天', '3', null, '2', '1', '职责描述：\n1、按照标准的日常操作流程，负责信息系统的日常开关机操作；\n2、根据运维管理规定，定时检查信息系统的运行情况；\n3、日常运维监控，出现监控报警必须尽快确认并上报运维管理岗；\n4、根据机房管理规定，定期执行机房环境和设备的巡检。 \n\n任职要求：\n1、2016或2017年应届毕业生；\n2、本科及以上学历，计算机相关专业；\n3、性格开朗，做事沉稳，抗压性强。', null, '1', null, null, null, null, '2017-12-11 16:30:45', '0', '1');
