INSERT INTO `xi_position` VALUES ('53', '57', '技术开发实习生', null, null, '上海陆家嘴', '2017-12-25', '2018-03-31', null, '18:00:00', '2000', '1800', '元/月', '3', null, '1', null, '职位描述:\n1、参与团队项目开发，主要业务包括支付、交易、营销活动、产品;\n2、配合参与应用的产品设计和需求分析；\n3、按照项目计划，按时提交高质量代码，完成开发任务；\n4、技术的学习和预研，对开源项目中可以应用到项目的技术进行可行性研究\n\n职位要求：\n1.熟悉面向对象编程技术概念（OOP），对Java或者.NET语言有基本了解，了解Java平台的开源框架：Spring、Spring MVC、MyBatis等； \n2.熟悉关系型数据库，掌握MySQL或者Oracle数据库的基本DDL、DML语法以及相关操作； \n3.熟悉基本的Web开发技术，了解CSS、Javascript、HTML，掌握前端框架的基本用法：jQuery、AngularJS； \n4.熟悉Linux环境，能够使用基本的命令操作系统资源； \n5.性格开朗活泼，善于沟通，积极乐观；', null, '2', null, null, null, null, '2017-12-17 13:32:04', '0', '1');
