INSERT INTO `xi_position` VALUES ('57', '69', '培训助理实习', null, null, '浦东新区福山路388号宏嘉大厦12F', '2018-01-01', '2018-03-01', '09:30:00', '17:30:00', '100', '90', '元/天', '3', null, '1', null, '岗位职责：\n1、协助领导做好部门培训支持工作工作，包括培训课程的协调、落实及追踪工作\n2、负责编制培训教材（ppt），收集经典案例；\n3、根据各分支机构情况，协助制定部门入职、在职、进阶等培训计划\n4、领导安排的其他工作\n任职要求：\n1、本科学历，每周至少工作3天(大三学生优先考虑)；\n2、具有较强的逻辑思维能力和沟通协调能力；\n3、具备良好的文字编写能力；\n 4、熟练运用OFFICE办公软件，擅长PPT制作\n', null, '1', null, null, null, null, '2017-12-19 12:39:03', '0', '1');
