INSERT INTO `xi_position` VALUES ('192', '149', '市场部实习生', '市场', null, '上海市浦东新区陆家嘴环路1333号平安金融大厦11楼', '2018-01-26', '2018-08-20', '09:00:00', '18:00:00', '100', '90', '元/天', '4', null, '1', '1', '岗位职责：\n1、进行保险、券商、银行等相关客户的业务拓展、关系维护与服务工作，包括各类业务对接、策划及营销推广活动等；\n2、进行保险、券商、银行等同业机构的市场调研，并分析统计市场状况；\n3、协同产品团队共同制定营销策略、产品方案和推广计划并落地实施；\n4、完成部门安排的其他工作。\n招聘要求：\n1、对市场分析、机构营销类工作感兴趣的在校研究生或本科生，全日制重点大学市场类专业者优先，每周可以保证3天出勤；\n2、有相关资产管理、私募基金公司实习经验者优先；\n3、具备良好的敬业精神，工作认真、踏实，高度的工作热情和良好的团队合作精神，能在较大压力下有效率地完成工作；\n4、具备良好的口头与文字表达能力、数据分析能力、人际沟通能力和团队协作精神。', null, '1', null, null, null, null, '2018-01-25 13:39:19', '0', '1');
