INSERT INTO `xi_position` VALUES ('289', '192', '市场经理（就业）', '市场', null, '上海市浦东新区杨高南路799号601室', '2018-05-10', '2018-12-31', '09:00:00', '17:00:00', '100', '90', '元/天', '4', null, '5', '1', '负责银行、证券公司、信托、保险等机构客户的开发、管理与维护；\n2.管理所负责机构的基金销售业务，制定销售计划，实现销售目标；\n3.负责跟踪行业内机构客户各类需求、结合公司投研情况针对性的制定客户服务方案，并反馈给客户；\n4.支持机构各类营销推广活动等。\n岗位要求：\n1. 金融、投资及其他相关专业本科及以上学历；\n2. 熟悉金融机构的业务流程；\n3. 有金融背景实习经验；\n4. 性格开朗，沟通能力强，有着良好的人际交往及客户关系维护能力。\n5.能承受较大的工作压力，有团队合作精神。', null, '1', null, null, null, null, '2018-05-10 14:32:04', '0', '1');
