INSERT INTO `xi_position` VALUES ('341', '202', '企业战略咨询部实习生（实习）', '市场', null, '上海市浦东新区福山路500号12楼', '2018-05-10', '2018-12-31', '09:00:00', '17:00:00', '100', '90', '元/天', '3', null, '4', '1', '1、支持企业运营数据分析和定期报告制作；\n2、支持业务发展部门开展绩效考评项目；\n3、支持改善措施跟进和效果分析;\n4、研讨会组织和会议纪要分析；\n5、领导委派的其他临时性任务；\n岗位要求：\n1、本科及以上在校生，有管理咨询行业实习经验最佳； \n2、熟练使用相关办公软件，尤其是PowerPoint，PPT；\n3、优秀的中英文沟通能力，互译流利；\n4、良好的数字敏感性和数据分析能力。', null, '1', null, null, null, null, '2018-05-10 16:13:20', '0', '1');
