INSERT INTO `xi_student` VALUES ('16', '49', '仇旖婷', '310230199601271926', '13916949165', '1', '1', '资产评估', null, 'baihe_yiting@126.com', null, '上海立信会计金融学院', '英语', '2', '4', 'http://www.xiangshixi.cc:-1/upload/STU_CARD/2017-11-22_11-47-10_2b09b.jpeg', '[\"http://www.xiangshixi.cc:-1/upload/STU_CERT/2017-11-22_11-57-46_a549a.jpeg\"]/java.util.ArrayList', null, '语言能力良好，有较强的团队精神，善于与他人合作 \n工作态度认真，做事严谨有责任心，遇到问题不逃避，愿意虚心学习\n热爱旅行，性格开朗，为人诚恳，对工作有热情', null, '崇明区城桥镇人民政府                                                   暑期实习生                                                                  2015年7月-8月\n接待来访群众与相关信息记录 || 文件处理与归档工作\n中国农业银行（崇明人民路支行）                                 大堂经理助理                                                               2016年7月-8月\n学习柜面相关知识，维持大堂秩序，提高服务效率 || 引导客户办理相关业务，培养沟通和营销能力\n上海骁天诚会计师事务所                                               审计部实习生                                                               2017年2月-3月\n使用财务软件完成200多笔交易的会计分录 || 抽查并复印记账凭证以及各类原始凭证和单据，整理审计工作底稿\n参与8家企业的年度审计工作 || 协助整理一家高新技术企业在税务局备案资料\n开云（中国）企业管理有限公司                                     财务部实习生                                                               2017年7月-9月\n付款申请单及银行水单的日常扫描与归档 || 月末凭证的整理与发票的检查\n企业品牌外勤与银行外勤 || 与品牌财务之间的基本邮件联系', null, null, '', null, '2017-11-22 11:31:33', '0');
INSERT INTO `xi_student` VALUES ('17', '68', 'Chico', '230103199603242833', '18149792929', '0', '1', '国际商务', null, '931398798@qq.com', null, '上海对外经贸大学', '英语', '1', '3', 'http://www.xiangshixi.cc/upload/STU_CARD/2017-11-30_22-52-44_bf57b.jpg', '[\"http://www.xiangshixi.cc/upload/STU_CERT/2017-11-30_22-52-48_60a86.JPG\"]/java.util.ArrayList', null, '1. 拥有傲视同侪的行业背景与经验\n2. 积极向上，勇于创新\n3. 做事需要更加仔细些~\n4. 明年7月会去澳洲完成本科最后一年学习，因此无法留用于很多实习岗位而惨遭拒绝\n5. 行业知识仍需完善\n', null, '1. 2 年外汇行业经验，熟练掌握 MT4 平台操作\n2. 联合创始人 & CEO：fxplutus.com\n3. 自营外汇网店——现已发展为淘宝最大 MT4 指标 EA 课程网店\n4. FXCM 福汇讲师工作经验\n5. 5000+ Linkedln 行业联系人\n6. 依靠提供相关外汇服务，大二起经济独立，每周固收0.5k~25k, 存款250k+\n7. 与行业内知名经纪商与媒体保持联系：\n    IG (James, Head of China)\n    FXCM (Max, EDU Manager)\n    IC Markets (Andrew, Director)\n    GMI (Alex, Institutional Sales)\n    IronFX (Angelo, BD Manager)\n    LCG (Jeff, Marketing Director)\n    FX Primus (Circa, Account Manager)\n    Orbex (Abdullah, CEO)\n    JFD Brokers (Lars, CEO)\n    FXunion (Guopei Lee, CEO)\n    FigureFinance (Mr Yu, CEO)\n8. 减重60斤~', null, null, 'http://www.xiangshixi.cc/upload/STU_AVATAR/2017-11-30_22-52-15_18cca.JPG', null, '2017-11-30 21:56:38', '0');
