INSERT INTO `xi_student` VALUES ('19', '70', '刘若璋', '320322199302200025', '18001613531', '1', '2', '金融', null, '214599473@qq.com', null, '上海对外经贸大学', '英语', '1', '3', 'http://xiangshixi.cc/upload/STU_CARD/2017-12-02_17-58-53_dbc8b.jpg', '[\"http://xiangshixi.cc/upload/STU_CERT/2017-12-02_17-58-59_2c22d.jpg\"]/java.util.ArrayList', null, '我待人友好，活泼外向，具有良好的沟通能力和抗压能力，能够吃苦耐劳。对待工作认真仔细踏实，适应能力强，有耐性，有责任心，有一定的学习能力。懂得自我管理、约束，注重效率和时间观念。', null, '2017.10~至今                 上海思勰投资管理有限公司              产品运营\n针对性地拟定合作方案、合作谈判、合同审核、后期的合作跟进与关系维护等\n产品推介、营销渠道投后服务支持、后期合同的管理，客户认购赎回资料的整理保管\n从产品合同归纳产品要素，根据产品要素制定相关产品风险等级制度、对公司产品进行估值\n2017.4~2017.9                   开源证券股份有限公司               投行部项目助理\n参加新三板推荐挂牌项目尽职调查、项目申报学习做项目过程中的财务、法律、行业等内容\n撰写会议记录、修改公开转让说明书以及编制公司底稿、根据审计数据进行公司行业水平分析\n参与北京、上海的两个项目，即将在新三板挂牌\n 2016.11~2017.3                  华宝证券股份有限公司             财富管理部互联网运营\n负责电商平台运营推广活动的策划及落地，协助运营指标的完成\n分析市场走势及竞争对手最新动态，根据市场热点，撰写公众号文章、制定话题内容传播方案 \n收集、整理、分析运营数据，根据运营数据分析结果挖掘用户痛点，持续策划和改进运营活动', null, null, '', null, '2017-12-02 17:55:41', '0');
